"// Mapping logic to generate the department and ensure it is always 1 character at least (empty value will become a space)
function generateDepartment() {
    let department = Person.PrimaryContract.Department.DisplayName;

    // Ensure the department field is at least 1 character long
    if (department === null || department.length < 1) {
        department = ' ';
    }

    return department;
}

